// IA Engine - Anรกlise de dores
function analyzeUserPain(painDescription) {
    const pain = painDescription.toLowerCase();
    console.log('๐ค Analisando dor:', pain);
    
    const scores = templates.map(template => {
        let score = 0;
        template.pain_match.forEach(keyword => {
            if (pain.includes(keyword)) {
                score += 1;
                console.log(`โ Match encontrado: "${keyword}" no template "${template.name}"`);
            }
        });
        return { ...template, score };
    });
    
    const results = scores
        .filter(template => template.score > 0)
        .sort((a, b) => b.score - a.score)
        .slice(0, 3);
        
    console.log('๐ฏ Templates recomendados:', results.map(t => `${t.name} (score: ${t.score})`));
    return results;
}

